package com.example.salawatime.presentation.ui.screen.todoimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.example.salawatime.data.todo.Taskimport com.example.salawatime.data.todo.repo.Repositoryimport dagger.hilt.android.lifecycle.HiltViewModelimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.launchimport javax.inject.Inject@HiltViewModelclass AddTaskViewModel @Inject constructor(private val repository: Repository) : ViewModel() {    fun addTask(task: Task){        viewModelScope.launch(Dispatchers.IO){            repository.addTask(task)        }    }}